  }
}

class Language {
  String name;
  List<Word> words;
  Language(String name) {
    this.name = name;
    words = new ArrayList<>();
  }

  public void addWord(Word w) {
    words.add(w);
  }

  public String toString() {
    StringBuilder sb = new StringBuilder();
    sb.append(name);
    sb.append(" (");
    for (Word w : words) {
      sb.append(w);
      sb.append(", ");
    }
    sb.append(")");
    return sb.toString();
  }
}

