```
contract Presale {
  address private seller;
  address public buyer;

  function sellTokens(uint256 amount) public {
    require(seller.address == buyer.address);
    buyer.transfer(seller.address, amount);
  }

  function sellTokens(address seller, uint256 amount) public {
    require(seller == buyer);
    buyer.transfer(seller, amount);
  }
}
```

