using namespace std; 

// Function to count the number of occurrences of a word in a file 
int countOccurrences(string word, ifstream &file) 
{ 
    int count = 0; 
    while (getline(file,word)) 
        count++; 
    return count; 
} 

// Function to read a text file line by line 
void readFile(ifstream &file) 
{ 
    string word; 
    while (getline(file,word)) 
    { 
        int count = countOccurrences(word, file); 
        cout << "The word " << word << " occurs " << count << "times in the file." << endl; 
    } 
} 

int main() 
{ 
    ifstream file("file.txt"); 
    readFile(file); 
    return 0; 
}

def findFibonacci(n): 
    if n<0: 
        print("Incorrect input") 
    if n==1: 
        return 0
    elif n==2: 
        return 1
    else: 
        temp = 0
        prev = 1
        curr = 1
        for i in range(3,n): 
            temp = prev + curr 
            prev = curr 
            curr = temp 
        return prev 

