class Queue:
    def __init__(self):
        self.Q1 = []
        self.Q2 = []

    def is_empty(self):
        return len(self.Q1) == 0 and len(self.Q2) == 0

    def enqueue(self, item):
        if not self.is_empty():
            self.Q2.append(item)
        else:
            self.Q1.append(item)

    def dequeue(self):
        if not self.is_empty():
            return self.Q1.pop()

    def peek(self):
        if not self.is_empty():
            return self.Q2[-1]

def findCommonElements(arr1, arr2): 
    # create an empty dictionary to store the common elements 
    common_elems = {} 

    # loop through the elements of both arrays 
    for i in range(len(arr1)): 
        # if the element is already in the dictionary, update the count 
        if i in common_elems: 
            common_elems[i] += 1
        # if the element is not present in the dictionary, add the element 
        else: 
            common_elems[i] = 1

    # loop through the elements of arr2 
    for j in range(len(arr2)): 
        # if the element is present in the dictionary, update the count 
        if j in common_elems: 
            common_elems[j] += 1
        # if the element is not present in the dictionary, add the element 
        else: 
            common_elems[j] = 1

    # return the common elements 
    return common_elems

