from string import lowercase

def long_common_subsequence(s1, s2):
    dp = [[0 for _ in range(len(s1) + 1)] for _ in range(len(s2) + 1)]
    for i in range(len(s1)):
        for j in range(len(s2), 0, -1):
            dp[i][j] = max(dp[i][j - 1], dp[i + 1][j] + 1)
    return dp[0][len(s2)]

print(long_common_subsequence(lowercase("aaba"), lowercase("abba")))

