#include <iostream>
#include <fstream>
using namespace std;

int main()
{
    // Open the file
    ifstream file;
    file.open("text.txt");
    if (!file.is_open())
    {
        cout << "Unable to open file." << endl;
        return 1;
    }

    // Declare variables
    int count = 0;
    string word;

    // Read each line from the file
    while (getline(file, word))
    {
        // Count the number of occurrences of the word in the line
        if (word.find(word) != string::npos)
        {
            count++;
        }
    }

    // Close the file
    file.close();

    // Print the result
    cout << "The word " << word << " occurs " << count << " times in the file." << endl;

