#include <iostream>

using namespace std;

int main()
{
    // Declare variables
    int count = 0;
    string word;

    // Open the file and read the lines
    ifstream file_stream("textfile.txt");
    while (getline(file_stream, word))
    {
        // Check if the word is found in the text file and increment the counter if it is.
        if (word == "hello")
        {
            count++;
        }
    }
    // Print the result
    cout << "The number of occurrences of the word 'hello' in the text file is " << count << endl;

def longest_common_subsequence(s, t):
    lcs = [[[0 for _ in range(len(s))]] for _ in range(len(t))]
    for i in range(len(s)):
        for j in range(len(t)):
            if i == j:
                lcs[i][j] = [s[i], t[j]]
                continue
            if s[i] == t[j]:
                lcs[i][j].append(s[i])
                continue
            lcs[i][j][0] = max(lcs[i][j][0], lcs[i][j][1])
            if lcs[i][j][0] > lcs[i][j][1]:
                lcs[i][j][1] = lcs[i][j][0]
            lcs[i][j][0] = t[j]
        return lcs[len(s) - 1][len(t) - 1]
