    # Create a matrix of length len(s1) + len(s2) with each cell being a Boolean value
    # indicating whether the current cell's subsequence exists in the string
    matrix = [False] * (len(s1) + len(s2))

    # Iterate through each possible subsequence and update the matrix
    for subsequence in possible_subsequences:
        for i in range(len(s1)):
            if subsequence[i] != s1[i]:
                matrix[i + len(s2)] = False
            else:
                matrix[i + len(s2)] = True

    # Iterate through each row of the matrix and find the longest common subsequence
    longest_common_subsequence = []
    for i in range(len(s1)):
        for j in range(len(s2)):
            if matrix[i + len(s2)][j] and matrix[i][j] and s1[i] == s2[j]:
                longest_common_subsequence.append(s1[i:j])

