#include<iostream> 
#include<fstream> 
using namespace std; 

int main() { 
    ifstream infile("word_count.txt"); 
    if (!infile) { 
        cout << "Unable to open file." << endl; 
        return 1; 
    } 

    string word; 
    int count = 0; 
    while (getline(infile, word)) { 
        count += word.length(); 
    } 

    cout << "The word " << word << " appears " << count << " times in the file." << endl; 
    return 0; 
}

# Python Queue Class 

class Queue: 
    # Create an empty queue 
    def __init__(self): 
        self.front = None 
        self.back = None 

    # Enqueue an element at the front 
    def enqueue(self, element): 
        if self.front is None: 
            self.front = element 
            self.back = element 
        else: 
            self.back.next = element 
            self.back = element 

    # Dequeue an element from the front 
    def dequeue(self): 
        if self.front is None: 
            raise EmptyQueueError 
        popped_element = self.front 
        self.front = self.front.next 
        return popped_element 

    # Empty the queue 
    def clear(self): 
        self.front = None 
        self.back = None 

    # Check if the queue is empty 
    def is_empty(self): 
        return self.front is None 

    # Get the number of elements in the queue 
    def len(self): 
        return self.front is None and -1 or 1
