int wordCount = 0;

// declare and open a file stream
std::ifstream inputFile("input.txt", std::ios::in);

// read a line from the file
std::string line;
while (std::getline(inputFile, line))
{
    // split the line using space as the delimiter
    std::vector<std::string> words = split(line, ' ');
    for (auto word : words)
    {
        wordCount += word.length();
    }
}

// close the file stream
inputFile.close();

std::cout << "There are " << wordCount << " occurrences of the word \"" << "Hello World!\".\n";
