#include <iostream>
#include <fstream>
using namespace std;

int CountWords(string filename, string word)
{
    ifstream fin(filename);
    int count = 0;
    while (fin >> word)
    {
        if (word == word)
        {
            ++count;
        }
    }
    return count;
}

