class Queue:
    def __init__(self):
        self.front = []
        self.rear = []
    def enqueue(self, item):
        self.front.append(item)
        if self.front == self.rear:
            self.rear = [None] + self.rear
        return self
    def dequeue(self):
        if self.front == self.rear:
            return None
        item = self.front.pop()
        self.rear = self.rear[1:] + [None]
        return item
    def is_empty(self):
        return self.front == []
```

