    int count = 0;

    // Read the first line of the file
    getline(inputFile, wordToCount);

    // While there is still text left in the file
    while (inputFile.good()){
        // Read the next line
        getline(inputFile, wordToCount);

        // If the current word is equal to the word we are counting
