def common_elements(array1, array2):
    # Create a set to store the common elements
    common_elements = set()

    # Loop over each element in both arrays
    for element1 in array1:
        # If the element is not already in the set, add it
        if element1 not in common_elements:
            common_elements.add(element1)

        for element2 in array2:
            # If the element is equal to the element1, add it to the set
