def longest_common_subsequence(s1, s2):
    if len(s1) == 0 or len(s2) == 0:
        return []
    if s1 == s2:
        return [s1]
    if len(s1) == 1:
        return [s1]
    if len(s2) == 1:
        return [s2]
    return longest_common_subsequence(s1[1:], s2[1:]) + [s1[0]] + longest_common_subsequence(s1[1:], s2[1:])
