def longestCommonSubsequence(s1, s2):
    m = [(0, 0)]
    for i in range(1, len(s1)):
        for j in range(1, len(s2)):
            if s1[i - 1] == s2[j - 1]:
                m[i - 1][j - 1] = max(m[i - 1][j - 1], m[i][j - 1]) + 1
            else:
                m[i][j - 1] = max(m[i][j - 1], m[i - 1][j]) + 1
    return m[len(s1)][len(s2)]
