def q(maxsize):
    class Queue:
        def __init__(self, maxsize):
            self.size = 0
            self.stacks = [[] for i in range(maxsize)]
        def enqueue(self, item):
            self.stacks[self.size].append(item)
            self.size += 1
        def dequeue(self):
            if self.size == 0:
                raise IndexError("Empty queue.")
            return self.stacks[self.size - 1][0]
