def longestCommonSubsequence(s1, s2):
    # Initialize a 0-length LCS with length 0
    LCS = [[] for i in range(len(s1))]
    LCS[0].append(s1[0])
    LCS[0].append(s2[0])
    for i in range(1, len(s1)):
        for j in range(1, len(s2)):
            if s1[i] == s2[j]:
                LCS[i].append(s2[j])
                LCS[j].append(s1[i])
                LCS[i+j].append(s1[i])
                LCS[j+i].append(s2[j])
                break
        else:
            LCS[i+j].append(s2[j])
            LCS[j+i].append(s1[i])
