#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <map>
#include <string>
using namespace std;
struct WordCount{
    int count;
};
vector<WordCount> wordCounts;
ifstream inputFile("words.txt");
string word;
while (inputFile >> word) { 
    if (wordCounts.empty()) { 
        wordCounts.push_back(WordCount{0}); 
    }
    wordCounts.back().count++; 
}
for (auto count : wordCounts) { 
    cout << count.count << endl; 
}
