#include<iostream>
#include<fstream>
#include<string>
#include<unordered_map>
#include<vector>
using namespace std;

int main(){
    ifstream fin;
    fin.open("D:/Users/user/Desktop/test.txt");
    if(!fin){
        cerr << "Unable to open the file";
        return 1;
    }
    string word;
    vector<string> lines;
    unordered_map<string, int> wordCount;
    while(getline(fin, lines[0]) ){
        for(int i = 0; i < lines[0].size(); i++){
            word += lines[0][i];
        }
        wordCount[word]++;
        word = "";
    }
    for(auto& it : wordCount){
        cout << it.first << " " << it.second << endl;
    }
    return 0;
}
