def lcs(s, t):
    dp = [[0 for _ in range(len(s))] for _ in range(len(t))]
    for i in range(len(s)):
        for j in range(len(t)):
            if s[i] == t[j]:
                dp[i][j] = dp[i-1][j-1]+1
    return dp[len(s)-1][len(t)-1]
