```
#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main()
{
    // Declare variables and set values.
    string word = "cat";
    int count = 0;
    ifstream inputFile;
    inputFile.open("myfile.txt");

    // Open file and read each line.
    while (getline(inputFile, line)) {
        // Find the occurrence of the word in the line.
        int position = line.find(word);
        if (position != string::npos) {
            // Increment the count.
            count++;
        }
    }
    inputFile.close();
    cout << "The word " << word << " occurs " << count << " times in the text file." << endl;

