    a, b = 0, 1
    for i in range(n - 2, 0, -1):
        a, b = b, a + b
    return a

def dp(n, a, b):
    if n == 0 or n == 1:
        return a, b
    a, b = dp(n - 1, a, b), dp(n - 2, a + b, a)
    return a, b

