Here is a program that counts the number of times a specific word is found in a text file.

#include <iostream>
#include <fstream>
#include <string>
using namespace std;

int main()
{
    //Declare variables and set values.
    string word = "hello";
    int count = 0;
    ifstream myFile("text.txt");
    if (myFile.is_open()) {
        while (!myFile.eof()) {
            getline(myFile, word);
            if (word == word) {
                count++;
            }
        }
        cout << "The number of occurrences of the word " << word << " in the text file " << count << endl;
    }
    myFile.close();
    return 0;

