def longest_common_subsequence(s1, s2):
    # Initialize the result as empty sequence
    result = []

    # Iterate over s1 and update result as you go
    for i in range(len(s1)):
        if i == 0 or s1[i] != s2[i]:
            # If the current character in s1 is different from the previous character in s2, add it to the result.
            result.append(s1[i])
        else:
            # If the current character in s1 is the same as the previous character in s2, check if the previous character in result is also the same as the previous character in s2.
            # If it is, then the current character in result is appended to the result.
            # Otherwise, the previous character in result is appended to the result and the current character in result is set to the current character in s1.
            if result[-1] == s2[i-1]:
                result.append(s1[i])
            else:
                result.append(s1[i])
                # Set the previous character in result to be the current character in s1.
                result[-1] = s1[i]

