#!/usr/bin/python -u

class Queue():
    def __init__(self):
        self.front = [None]
        self.back = [None]

    def enqueue(self, item):
        if not self.full():
            self.front[0] = item
        else:
            self.back[0] = item
        return True

    def dequeue(self):
        if not self.empty():
            return self.front.pop(0)
