def longest_common_subsequence(s, t):"""Returns the longest common subsequence of two strings s and t."""
    def get_longest_common_subsequence(s, t):
        if len(s) == 0 or len(t) == 0:
            return []
        else:
            result = [s[0]]
            for i in range(1, len(s)):
                for j in range(1, len(t)):
                    if s[i - 1] == t[j - 1]:
                        result.extend(get_longest_common_subsequence(s[1:i], t[1:j]))
                    else:
                        result.extend(get_longest_common_subsequence(s[1:i], t[j]))
            return result
        return get_longest_common_subsequence(s[1:], t[1:])
    return longest_common_subsequence(s, t)

def find_comm_elems(arr1, arr2): 
    comm_elems = set()
    for i in range(len(arr1)):
        if arr1[i] in comm_elems:
            continue
        comm_elems.add(arr1[i])
    for i in range(len(arr2)):
