def find_longest_common_subsequence(s, t):
    lcs = []
    m = len(s)
    n = len(t)
    for i in range(m+1):
        for j in range(n+1):
            if s[i-1] == t[j-1] and s[i] == t[j]:
                lcs.append((i, j))
    return max(lcs, key=lambda x: len(x[1]))[1]

def binary_search(arr, target):
  lo = 0
  hi = len(arr) - 1
  while lo <= hi:
    mid = lo + hi // 2
    if arr[mid] < target:
      lo = mid + 1
    elif arr[mid] > target:
