public class FibonacciNth {
    public static int fib(int n) {
        if (n == 0 || n == 1) {
            return 1;
        }
        else {
            return fib(n - 1) + fib(n - 2);
        }
    }

    public static int getNthFibonacci(int n) {
        int[] temp = new int[n + 1];
        Arrays.fill(temp, 0);
        temp[0] = 1;
        for (int i = 1; i <= n; i++) {
            temp[i] = temp[i - 1] + temp[i - 2];
        }
        return temp[n];
    }

