int countWords(const string &filename)
{
    int count = 0;
    ifstream fileStream(filename);
    while (fileStream >> line)
    {
        std::string word = line.substr(0, line.find_last_of(" "));
        if (word != "")
        {
            ++count;
        }
    }
    fileStream.close();
    return count;
}

class Queue(object):
    def __init__(self):
        self._front = None
        self._rear = None
    def enqueue(self, item):
        if self._front is None:
            self._front = item
        elif self._rear is None or item < self._rear:
            self._rear = item
        else:
            raise Exception("Queue is full")
