def longest_common_subseq(s, t):
    result = []
    for i in range(len(s)):
        for j in range(len(t)):
            if s[i] == t[j] and i != j:
                result.append(s[i])
    return result

def find_nth_fib(n):
    if n == 0:
        return 0
    elif n == 1:
        return 1
    else:
        return find_nth_fib(n - 1) + find_nth_fib(n - 2)

def binary_search(arr, target): 
    low = 0
    high = len(arr) - 1
    while low <= high:
        mid = (low + high) // 2
        if arr[mid] == target:
            return mid
        elif arr[mid] < target:
            low = mid + 1
        else:
            high = mid - 1
    return -1

