#include<iostream>
#include<fstream>
#include<string>
#include<vector>
using namespace std;

int main() {
    ifstream inputFile;
    inputFile.open("text.txt");
    vector<string> words;
    int count = 0;
    while (inputFile.good()) {
        string word;
        getline(inputFile, word);
        words.push_back(word);
        count++;
    }
    for (int i = 0; i < words.size(); i++) {
        cout << words[i] << " Occurred " << count << " times" << endl;
    }
    return 0;

def binary_search(arr, item):
    low = 0
    high = len(arr) - 1
    while low <= high:
        mid = (low + high) // 2
        if arr[mid] == item:
            return mid
        elif arr[mid] < item:
