def find_longest_common_subsequence(s, t):
    m = [s]
    for i in range(len(t)):
        m.append(s + t[i])

    curr = [s]
    for i in range(len(t)):
        curr.append(0)
        for j in range(len(m)):
            if m[j] == t[i]:
                curr[j] += 1
    return curr[len(t) - 1]

def binary_search(array, element):
    low = 0
    high = len(array) - 1
    while low <= high:
        mid = (low + high) // 2
        if array[mid] == element:
            return mid
        else if array[mid] < element:
            low = mid + 1
        else:
            high = mid - 1
    return -1

