def binary_search(A, x): 
    # Sort the array
    A = sorted(A) 

    # Find the middle element of the array
    m = len(A) // 2

    # If the element is not in the array, return -1
    if x != A[m]:
        return -1

    # Compare the element with the middle element
    if A[m] < x:
        return binary_search(A, x, m+1)
    elif A[m] > x:
        return binary_search(A, x, m-1)
    else:
        return m

