def longest_common_subsequence(s, t):
    # Find the length of the longest common subsequence.
    ls = 0
    for i in range(len(s)):
        for j in range(len(t)):
            if s[i] == t[j] and s[i + 1:] == t[j + 1:]:
                ls = max(ls, i + j - 1)
    return ls
