// C++ Program to Count the Number of Occurrences of a Word in a Text File

#include <iostream>
#include <fstream>
#include <string>
#include <cstdlib>

using namespace std;

int main() {

    // Declare variables
    string word;
    int count = 0;

    // Open the text file
    ifstream fileStream;
    fileStream.open("textFile.txt");

    // Check if file opened successfully
    if (fileStream.fail()) {
        cerr << "Error: Unable to open the text file.\n";
        return 1;
    }

    // Read the first line of the file
    getline(fileStream, word);

    // Loop until the end of the file
    while (fileStream >> word) {
        // Increment the count
        count++;
    }

    // Close the file stream
    fileStream.close();

    // Print the result
    cout << "The number of occurrences of the word " << word << " in the text file is " << count << "\n";

    // Return 0 to indicate successful completion of the program
    return 0;
}
