public class QuickSort
{
    public static void Sort(int[] arr)
    {
        // Quick sort algorithm 
        int n = arr.Length;
        int low = 0;
        int high = n-1;

        // Choose a pivot 
        int pivot = arr[high]; 

        // Partition the array 
        while (low < high) 
        { 
            // Move elements from left sub array 
            // to right sub array 
            int temp = arr[low]; 
            arr[low] = arr[high]; 
            arr[high] = temp; 

            // Partition the array 
            if (arr[low] < pivot) 
            { 
                low++; 
            } 
            else if (arr[low] > pivot) 
            { 
                high--; 
            } 
            // Recursively sort the subarrays 
            Sort(arr, low, high); 
        }
    } 
}

