public static List<T> ReadExcel<T>(string filePath) where T : new()
{
    var package = new ExcelPackage(new FileInfo(filePath));
    var worksheet = package.Workbook.Worksheets[1];
    var table = worksheet.Tables.First();

    var result = new List<T>();

    var columns = table.Columns.Cast<ExcelColumn>().ToList();

    foreach (var row in table.Rows)
    {
        var obj = new T();

        foreach (var col in columns)
        {
            obj.SetValue(col.Name, row[col.Index]);
        }

        result.Add(obj);
    }

    return result;
}

public class ExcelColumn
{
    public string Name { get; set; }
    public int Index { get; set; }
}

public class T
{
    public ExcelColumn[] Columns { get; set; }

    public void SetValue(string name, object value)
    {
        if (name == null) throw new ArgumentNullException(nameof(name));
        if (value == null) throw new ArgumentNullException(nameof(value));

        this.Columns = new ExcelColumn[]{
                new ExcelColumn { Name = name, Index = 0 },
                new ExcelColumn { Name = "Id", Index = 1 },
                new ExcelColumn { Name = "Name", Index = 2 },
                new ExcelColumn { Name = "Gender", Index = 3 },
        };

        this.Columns[0].SetValue(value);
        this.Columns[1].SetValue(value);
        this.Columns[2].SetValue(value);
        this.Columns[3].SetValue(value);
    }
}
\end{code}

