public static void QuickSort(int[] array) {
    int n = array.Length;
    if (n < 2) return;
    else {
        int pivot = array[n - 1];
        int left = 0;
        int right = n - 1;
        while (left < right) {
            while (left < n && array[left] < pivot) left++;
            while (right > 0 && array[right - 1] > pivot) right--;
            if (left >= right) break;
            int t = left;
            left = right;
            right = t - 1;
        }
        swap(array, left, right);
        QuickSort(array, left, n - 1);
        swap(array, right, n - 1);
        QuickSort(array, n - 1, 0);
    }
}

