void SortUnsortedArray(int[] array)
{
    int pivot = array[0];
    int left = 0, right = array.Length - 1;
    while (left < right)
    {
        int compareValue = array[left].CompareTo(pivot);
        if (compareValue < 0)
        {
            left++;
        }
        else if (compareValue > 0)
        {
            right--;
        }
        else
        {
            Swap(ref array[left], ref array[right]);
            left++;
            right--;
        }
    }

    Swap(ref array[left], ref array[right]);
    SortUnsortedArray(array, left, right - 1);
}

private static void Swap<T>(ref T first, ref T second)
{
    T temp = first;
    first = second;
    second = temp;
}

