// Sort the array using the Quick Sort algorithm.

public void Sort(int[] array)
{
    // Choose a pivot element from the array.
    int pivot = array[0];
    int left = 0;
    int right = array.Length - 1;
    while (left < right)
    {
        // If the element is less than the pivot, move it to the left subarray.
        if (array[left] <= pivot)
        {
            array[left] = array[left];
            left++;
        }
        // If the element is greater than the pivot, move it to the right subarray.
        else if (array[right] > pivot)
        {
            array[right] = array[right];
            right--;
        }
        // If the element is equal to the pivot, no action is needed.
        else
        {
            break;
        }
    }

    // Recursively sort the left and right subarrays.
    Sort(array, left, right - 1);
    Sort(array, left + 1, right);
}

