public class LongestCommonSubsequence { 
    public static String longestCommonSubsequence(String s1, String s2) {
        if (s1.length() == 0 || s2.length() == 0) {
            return "";
        }

        int m = s1.length();
        int n = s2.length();
        int[][] matrix = new int[m][n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (s1.charAt(i) == s2.charAt(j)) {
                    matrix[i][j] = 1;
                } else {
                    matrix[i][j] = 0;
                }
            }
        }

        // Breadth-first search
        int[] path = new int[m];
        int[] last = new int[n];
        int current = 0;
        int next = 0;
        int maxLength = 0;
        int[][] result = new int[m][n];

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (matrix[i][j] == 1) {
                    if (current == 0) {
                        path[i] = j;
                    }
                    last[j] = i;
                }
            }
        }

        while (current < m) {
            if (maxLength < path[current]) {
                maxLength = path[current];
            }
            next = last[current];
            current = next;
            result[current][next] = maxLength;

            for (int i = 0; i < m; i++) {
                if (last[i] == current) {
                    path[i] = next;
                }
            }
        }

        return result[0][0];
    }
}

