public class LongestCommonSubsequence {
    public static void main(String[] args) {
        String str1 = "ABCDE";
        String str2 = "ABCDGH";
        String[] str1Array = str1.split("");
        String[] str2Array = str2.split("");
        int[][] lcsMatrix = new int[str1Array.length][str2Array.length];
        for (int i = 0; i < str1Array.length; i++) {
            for (int j = 0; j < str2Array.length; j++) {
                if (str1Array[i] == str2Array[j]) {
                    lcsMatrix[i][j] = (int) Math.max(lcsMatrix[i][j], lcsMatrix[i + 1][j] + 1);
                } else {
                    lcsMatrix[i][j] = (int) Math.max(lcsMatrix[i][j], lcsMatrix[i][j + 1] + 1);
                }
            }
        }
        for (int i = 0; i < lcsMatrix.length; i++) {
            for (int j = 0; j < lcsMatrix[i].length; j++) {
                if (lcsMatrix[i][j] > lcsMatrix[i][j + 1]) {
                    System.out.println(String.format("LCS is %s and length is %d", str1Array[i] + str2Array[j], lcsMatrix[i][j]));
                    break;
                }
            }
        }
    }
}

