public static int[] productExceptSelf(int[] nums, int[] output) 
{
    int length = nums.length;
    if (length == 1) {
        output[0] = nums[0];
        return output;
    }

    int product = nums[0];
    for (int i = 1; i < length - 1; i++) {
        product = product * nums[i];
    }
    output[length - 1] = product;
    return output;
}

