void quicksort(int nums[], int low, int high) {
    if (low < high) {
        int piv = nums[high];
        int i = low - 1;
        int j = high;

        while (i <= j) {
            while (nums[j] > piv) {
                j--;
            }
            while (nums[i] < piv) {
                i++;
            }
            if (nums[j] > nums[i]) {
                swap(nums[j], nums[i]);
                j--;
                i++;
            }
        }
        quicksort(nums, low, j);
        quicksort(nums, j + 1, high);
    }
}

