The following code implementation has a time complexity of O(nlog(n)) and a space complexity of O(1).

#include <iostream>
using namespace std;

void quicksort(int nums[], int low, int high) {
    if (low < high) {
        int pivot = nums[high];
        int i = low - 1;

        for (int j = low; j <= high - 1; j++) {
            if (nums[j] <= pivot) {
                i++;
                swap(nums[i], nums[j]);
            }
        }
        swap(nums[i + 1], nums[high]);
        quicksort(nums, low, i - 1);
        quicksort(nums, i + 1, high);
    }
}

