public boolean isPalindrome(String str) {
    if (str == null || str.length() == 0) {
        return false;
    }

    for (int i = 0; i < str.length()/2; i++) {
        if (str.charAt(i) != str.charAt(str.length() - i - 1)) {
            return false;
        }
    }

    return true;
}

