public static void Sort<T>(IList<T> array, IComparer<T> comparer)
    {
        if (array == null || array.Count == 0)
        {
            return;
        }

        T pivot = array[0];
        int left = 0;
        int right = array.Count - 1;

        while (left < right)
        {
            int mid = left + right >> 1;
            comparer.Compare(pivot, array[mid]) <= 0 ? right = mid - 1 : left = mid + 1;
        }

        if (left < right)
        {
            Swap(array, left, right);
        }

        Sort(array, comparer);

        Swap(array, 0, pivot);
    }

    public static void Swap(IList<T> array, int left, int right)
    {
        T value = array[left];
        array[left] = array[right];
        array[right] = value;
    }

