        if (seq1.length() == 0) return seq2;
        if (seq2.length() == 0) return seq1;

        final int m = Math.min(seq1.length(), seq2.length());
        final StringBuilder sb = new StringBuilder();
        for (int i = 0; i < m; i++) {
            final char c1 = seq1.charAt(i);
            final char c2 = seq2.charAt(i);
            if (c1 == c2) {
                sb.append(c1);
            } else {
                break;
            }
        }
        return sb.toString();
    }
}

