#include <stdio.h>
#include <stdlib.h>
#include <string.h>

void reverse_in_place(struct node *head) {
    struct node *temp;
    if (head == NULL) {
        return;
    }
    temp = head;
    while (temp->next) {
        temp->next = temp->next->next;
        temp = temp->next;
    }
    temp->next = NULL;
}

